import React from 'react';
import Ptext from './Ptext';
import SectionTitle from './SectionTitle';
import Button from './Button';
import AboutImg from '../assets/images/about-sec-img.png';
import styled from 'styled-components';

const AboutSectionStyle = styled.div `
padding: 10rem 0;
  .container {
    display: flex;
    align-items: center;
    justify-content: flex-start;
    text-align: left;
  }
  .aboutSection_left,
  .aboutSection_right {
    flex: 1;
  }
  .section-title {
    text-align: left;
  }
  .para {
    margin-top: 2rem;
    margin-left: 0;
  }
  .abs_buttons {
    display: flex;
    align-items: center;
    justify-content: flex-start;
    gap: 2rem;
    margin-top: 2rem;
  }
`;


export default function AboutSection() {
  return (
    <div>
      <div className="container">
        <div className="aboutSection_left">
          <SectionTitle
            className="section-title"
            subheading="Let me introduce myself"
            heading="About me"
          />
          <Ptext>
            I am a freelance website designer and developer from Chittagong,
            Bangladesh. I create professional websites. I love art and always
            try to show unique views to the audience through my design.
          </Ptext>
          <div className="abs_buttons">
            <Button btnLink="/projects" btnText="Works" />
            <Button btnLink="/about" btnText="Read More" outline />
          </div>
        </div>
        <div className="aboutSection_right">
          <img src={AboutImg} alt="" />
        </div>
      </div>
    </div>
  );
}
