/* eslint-disable no-plusplus */
/* eslint-disable no-unused-vars */
/* eslint-disable no-use-before-define */
/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable jsx-a11y/no-static-element-interactions */
import React from 'react';
import styled from 'styled-components';

const TestiStyles = styled.div`
  .slideshow-container {
    position: relative;
    background: #f1f1f1f1;
  }

  .mySlides {
    display: none;
    padding: 80px;
    text-align: center;
  }

  .prev,
  .next {
    cursor: pointer;
    position: absolute;
    top: 50%;
    width: auto;
    margin-top: -30px;
    padding: 16px;
    color: #888;
    font-weight: bold;
    font-size: 20px;
    border-radius: 0 3px 3px 0;
    user-select: none;
  }

  .next {
    position: absolute;
    right: 0;
    border-radius: 3px 0 0 3px;
  }

  .prev:hover,
  .next:hover {
    background-color: rgba(0, 0, 0, 0.8);
    color: white;
  }

  .dot-container {
    text-align: center;
    padding: 20px;
    background: #ddd;
  }

  .dot {
    cursor: pointer;
    height: 15px;
    width: 15px;
    margin: 0 2px;
    background-color: #bbb;
    border-radius: 50%;
    display: inline-block;
    transition: background-color 0.6s ease;
  }

  .active,
  .dot:hover {
    background-color: #717171;
  }

  q {
    font-style: italic;
  }

  .author {
    color: cornflowerblue;
  }
`;

let slideIndex = 1;
showSlides(slideIndex);

function plusSlides(n) {
  showSlides((slideIndex += n));
}

function currentSlide(n) {
  showSlides((slideIndex = n));
}

function showSlides(n) {
  let i;
  const slides = document.getElementsByClassName('mySlides');
  const dots = document.getElementsByClassName('dot');
  if (n > slides.length) {
    slideIndex = 1;
  }
  if (n < 1) {
    slideIndex = slides.length;
  }
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = 'none';
  }
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(' active', '');
  }
  slides[slideIndex - 1].style.display = 'block';
  dots[slideIndex - 1].className += ' active';
}

export default function Testi() {
  return (
    <TestiStyles>
      <div className="slideshow-container">
        <div className="mySlides">
          <q>
            I love you the more in that I believe you had liked me for my own
            sake and for nothing else
          </q>
          <p className="author">- John Keats</p>
        </div>

        <div className="mySlides">
          <q>
            But man is not made for defeat. A man can be destroyed but not
            defeated.
          </q>
          <p className="author">- Ernest Hemingway</p>
        </div>

        <div className="mySlides">
          <q>I have not failed. I've just found 10,000 ways that won't work.</q>
          <p className="author">- Thomas A. Edison</p>
        </div>

        <a className="prev" onClick="plusSlides(-1)">
          &#10094;
        </a>
        <a className="next" onClick="plusSlides(1)">
          &#10095;
        </a>
      </div>

      <div className="dot-container">
        <span className="dot" onClick="currentSlide(1)" />
        <span className="dot" onClick="currentSlide(2)" />
        <span className="dot" onClick="currentSlide(3)" />
      </div>
    </TestiStyles>
  );
}
