import React from 'react';
import styled from 'styled-components';
import Ptext from '../components/Ptext';
import Button from '../components/Button';
import AboutImg from '../assets/images/about-page-img.png';
import Aboutinfoitem from '../components/Aboutinfoitem';

const AboutsectionStyle = styled.div`
  padding: 20rem 0 10rem 0;
  .top-section {
    display: flex;
    align-content: center;
    justify-content: center;
    gap: 2rem;
  }
  .left {
    flex: 3;
  }
  .right {
    flex: 2;
    img {
      border: 2px solid var(--gray-1);
    }
  }
  .about_subheading {
    font-size: 2.2rem;
    margin-bottom: 2rem;
    span {
      background-color: var(--deep-dark);
      padding: 0.5rem;
      border-radius: 8px;
    }
  }
  .about_heading {
    font-size: 3.6rem;
    margin-bottom: 3rem;
  }
  .about_info {
    margin-bottom: 4rem;
    .para {
      max-width: 100%;
    }
  }
  .about_info_items {
    margin-top: 15rem;
  }
  .about_info_item {
    margin-bottom: 10rem;
  }
  .about_info_heading {
    font-size: 3.6rem;
    text-transform: uppercase;
  }
  @media only screen and (max-width: 768px) {
    padding: 10rem 0;
    .top-section {
      flex-direction: column;
      gap: 5rem;
    }
    .about_subheading {
      font-size: 1.8rem;
    }
    .about_heading {
      font-size: 2.8rem;
    }
    .c {
      font-size: 3rem;
    }
  }
`;

export default function About() {
  return (
    <AboutsectionStyle>
      <div className="container">
        <div className="top-section">
          <div className="left">
            <p className="about_subheading">
              Hi, I am <span>Sambhav Jain</span>
            </p>
            <h2 className="about_heading">Web Developer</h2>
            <div className="about_info">
              <Ptext>
                I am from chittagong, Bangladesh. A place of beauty and nature.
                Since my childhood, i love art and design. I always try to
                design stuff with my unique point of view. I also love to create
                things that can be usefull to others.
                <br /> <br />
                I started coding since I was in high school. Coding is also an
                art for me. I love it and now I have the opportunity to design
                along with the coding. I find it really interesting and I
                enjoyed the process a lot.
                <br />
                <br />
                My vision is to make the world a better place. Now almost
                everything is becoming better than ever. It is time for us to
                create more good stuff that helps the world to become a better
                place.
              </Ptext>
            </div>
            <Button btnText="Download CV" btnLink="#" />
          </div>
          <div className="right">
            <img src={AboutImg} alt="me" />
          </div>
        </div>
        <div className="about_info_items">
          <div className="about_info_item">
            <h1 className="about_info_heading">Education</h1>
            <Aboutinfoitem
              title="School"
              items={['Nasirabad Govt. High School, Chattogram']}
            />
            <Aboutinfoitem
              title="Collage"
              items={['BAF Shaheen College Chattogram']}
            />
          </div>
          <div className="about_info_item">
            <h1 className="about_info_heading">My Skills</h1>

            <Aboutinfoitem
              title="FrontEnd"
              items={['HTML', 'CSS', 'JavaScript', 'REACT']}
            />
            <Aboutinfoitem title="BackEnd" items={['Node', 'Express', 'PHP']} />
            <Aboutinfoitem
              title="Design"
              items={['Photoshop', 'After Effects', 'Figma']}
            />
          </div>
          <div className="about_info_item">
            <h1 className="about_info_heading">Experiences</h1>

            <Aboutinfoitem
              title="2010-2012"
              items={['junior developer at web Cifar']}
            />
            <Aboutinfoitem
              title="2012-2016"
              items={['Front end developer at web Cifar ']}
            />
            <Aboutinfoitem title="2016-" items={['Freelance web Developer']} />
          </div>
        </div>
      </div>
    </AboutsectionStyle>
  );
}
