import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { MdSearch } from 'react-icons/md';
import SectionTitle from '../components/SectionTitle';
import ProjectItem from '../components/ProjectItem';
import ProjectInfo from '../assets/data/projects';

const ProjectStyle = styled.div`
  padding: 10rem;
  .Project_items {
    display: grid;
    grid-template: repeat(auto-fill, minmax(300px, 1fr));
    gap: 5rem;
    margin-top: 5rem;
  }
  .Project_searchbar {
    position: relative;
    width: 300px;
    margin-top: 5rem;
  }
  .Project_searchBar input {
    width: 100%;
    font-size: 2rem;
    padding: 0.8rem;
    color: var(--black);
    border-radius: 6px;
    outline: none;
    border: none;
  }
  .Project_searchBar .searchIcon {
    position: absolute;
    width: 2rem;
    right: 1rem;
  }
  .Project_searchBar .searchIcon path {
    color: var(--deep-dark);
  }
  @media only screen and (max-width: 768px) {
    .Project_searchBar,
    .Project_searchBar form,
    .Project_searchBar input {
      width: 100%;
    }
  }
`;

export default function Project() {
  const [searchText, setseacrhText] = useState('');
  const [projectData, setProjectData] = useState(ProjectInfo);

  useEffect(() => {
    if (searchText === '') return;
    setProjectData(() =>
      ProjectInfo.filter((item) =>
        item.name.toLowerCase().match(searchText.toLowerCase())
      )
    );
  }, [searchText]);

  const handleChange = (e) => {
    e.preventDefault();
    setseacrhText(e.target.value);
    if (!e.target.value.length > 0) {
      setProjectData(ProjectsInfo);
    }
  };

  return (
    <ProjectStyle>
      <div className="container">
        <SectionTitle subheading="some of my recent works" heading="Projects" />
        <div className="Project_searchbar">
          <form>
            <input
              type="text"
              value={searchText}
              onChange={handleChange}
              placeholder="Project Name"
            />
            <MdSearch className="searchIcon" />
          </form>
        </div>
        <div className="Project_items">
          {projectData.map((item) => (
            <ProjectItem
              key={item.id}
              title={item.title}
              desc={item.desc}
              img={item.img}
            />
          ))}
        </div>
      </div>
    </ProjectStyle>
  );
}
